import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {id:0,name:'Jerry'},
      {id:1,name:'Lucy'},
      {id:2,name:'Alexxx'}
    ],
    isShow:true
  }
  //双向绑定
  changeValue = (event,id)=>{
    //找到当前修改的person的id
    const personIndex = this.state.person.findIndex((per)=>{
      return per.id === id;
    })
    //当前修改的person对象
    const person = this.state.person[personIndex];
    //绑定输入的值
    person.name = event.target.value;
    //重新赋给state里面的person
    const persons = [...this.state.person];
    persons[personIndex] = person;
    this.setState({
      person:persons
    })
  }

  //删除
  deleteIt = (id)=>{
    const persons = [...this.state.person];
    persons.forEach((item,index)=>{
      if(item.id === id){
        persons.splice(index,1);
      }
    })
    this.setState({
      person:persons
    })
  }
  //显示隐藏
  toggleCt = ()=>{
    this.setState({
      isShow:!this.state.isShow
    })
  }

  render(){
    const btnStyle = {
      padding:10
    }
    let per = this.state.person.map((per,index)=>{
      return <Person
      key = {per.id}
      name = {per.name}
      changeValue = {(event)=>{this.changeValue(event,per.id)}}
      onClick = {()=>{this.deleteIt(per.id)}}
      />
    })
    let person = null;
    if(this.state.isShow){
      person = <div>
        {per}
      </div>
    }
    return (
      <div className="App">
        <h1>Hello React!</h1>
        <button style = {btnStyle} onClick = {this.toggleCt}>点击显示/隐藏</button>
        {person}
      </div>
    );
  }
}

export default App;
