import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {name:'Jerry'},
      {name:'Lucy'},
      {name:'Alexxx'},
    ],
    content:"this is content~"
  }

  updateName = (name)=>{
    this.setState({
      person:[
        {name:name},
        {name:'Lucy'},
        {name:'Alex'},
      ],
    })
  }
  render(){
    return (
      <div className="App">
        <h1>Hello React!</h1>
        <button onClick = {this.updateName.bind(this,'hhahah')}>click to change</button>
        <Person name = {this.state.person[0].name}/>
        <Person name = {this.state.person[1].name}/>
        <Person name = {this.state.person[2].name}>{this.state.content}</Person>
      </div>
    );
  }
}

export default App;
