import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {id:0,name:'Jerry'},
      {id:1,name:'Lucy'},
      {id:2,name:'Alexxx'}
    ],
    isShow:true
  }
  //双向绑定
  changeValue = (event,id)=>{
    event.stopPropagation();
    e.nativeEvent.stopImmediatePropagation();
    //找到当前修改的person的id
    const personIndex = this.state.person.findIndex((per)=>{
      return per.id === id;
    })
    //当前修改的person对象
    const person = this.state.person[personIndex];
    //绑定输入的值
    person.name = event.target.value;
    //重新赋给state里面的person
    const persons = [...this.state.person];
    persons[personIndex] = person;
    this.setState({
      person:persons
    })
  }

  //删除
  deleteIt = (id)=>{
    const persons = [...this.state.person];
    persons.forEach((item,index)=>{
      if(item.id === id){
        persons.splice(index,1);
      }
    })
    this.setState({
      person:persons
    })
  }
  //显示隐藏
  toggleCt = ()=>{
    this.setState({
      isShow:!this.state.isShow
    })
  }

  render(){
    const btnStyle = {
      padding:10,
      color:'#fff',
      backgroundColor:'#449933',
      outline:'none'
    }
    let person = null;
    if(this.state.isShow){
      person = <div>
        {
          this.state.person.map((per,index)=>{
            return <Person
            key = {per.id}
            name = {per.name}
            changeValue = {(event)=>{this.changeValue(event,per.id)}}
            deleteIt = {()=>{this.deleteIt(per.id)}}
            />
          })
        }
      </div>

      btnStyle.backgroundColor = '#dd6677'
    }
    let titleStyle = [];
    if(this.state.person.length<=2){
      titleStyle.push('fontC');
    }
    if(this.state.person.length<=1){
      titleStyle.push('fontC','fontW');
    }
    return (
      <div className="App">
        <h1 className = {titleStyle.join(" ")}>Hello React!</h1>
        <button style = {btnStyle} onClick = {this.toggleCt}>点击显示/隐藏</button>
        {person}
      </div>
    );
  }
}

export default App;
