import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {name:'Jerry'},
      {name:'Lucy'},
      {name:'Alexxx'},
    ]
  }
  changeValue = (event)=>{
    this.setState({
      person:[
        {name:event.target.value},
        {name:'Lucy'},
        {name:'Alexxx'}
      ]
    })
  }
  render(){
    const btnStyle = {
      padding:10
    }
    return (
      <div className="App">
        <h1>Hello React!</h1>
        <button style = {btnStyle}>点击显示/隐藏</button>
        <Person name = {this.state.person[0].name} changeValue = {(event)=>{this.changeValue(event)}}/>
        <Person name = {this.state.person[1].name}/>
        <Person showTip = {this.showTip} name = {this.state.person[2].name}>{this.state.content}</Person>
      </div>
    );
  }
}

export default App;
