import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {name:'Jerry'},
      {name:'Lucy'},
      {name:'Alexxx'},
    ],
    isShow:true
  }
  changeValue = (event)=>{
    this.setState({
      person:[
        {name:event.target.value},
        {name:'Lucy'},
        {name:'Alexxx'}
      ]
    })
  }
  toggleCt = ()=>{
    this.setState({
      isShow:!this.state.isShow
    })
  }
  render(){
    const btnStyle = {
      padding:10
    }
    let person = null;
    if(this.state.isShow){
      person = <div className="App">
      <h1>Hello React!</h1>
      <button style = {btnStyle} onClick = {this.toggleCt}>点击显示/隐藏</button>
      <Person name = {this.state.person[0].name} changeValue = {(event)=>{this.changeValue(event)}}/>
      <Person name = {this.state.person[1].name}/>
      <Person showTip = {this.showTip} name = {this.state.person[2].name}>{this.state.content}</Person>
    </div>
    }
    return (
      person
    );
  }
}

export default App;
