import React ,{Component}from 'react';
import Person from './Person/Person'
import './App.css';

class App extends Component{
  state = {
    person:[
      {id:0,name:'Jerry'},
      {id:1,name:'Lucy'},
      {id:2,name:'Alexxx'},
    ],
    isShow:true
  }
  changeValue = (event)=>{
    this.setState({
      person:[
        {name:event.target.value},
        {name:'Lucy'},
        {name:'Alexxx'}
      ]
    })
  }
  toggleCt = ()=>{
    this.setState({
      isShow:!this.state.isShow
    })
  }
  render(){
    const btnStyle = {
      padding:10
    }
    let person = null;
    if(this.state.isShow){
      person = <div>
        <Person name = {this.state.person[0].name} changeValue = {(event)=>{this.changeValue(event)}}/>
        <Person name = {this.state.person[1].name}/>
        <Person name = {this.state.person[2].name}/>
      </div>
    }
    return (
      <div className="App">
        <h1>Hello React!</h1>
        <button style = {btnStyle} onClick = {this.toggleCt}>点击显示/隐藏</button>
        {person}
      </div>
    );
  }
}

export default App;
