import React from 'react'
import './Person.css'
const person = (props)=>{
    var stopFn = (event)=>{
        event.stopPropagation();
        event.nativeEvent.stopImmediatePropagation();
    }
    return(
        <div onClick = {props.deleteIt} className = "Personbox">
            <h3>我的名字叫{props.name}</h3>
            <input type = "text" defaultValue = {props.name} onClick={(event)=>stopFn(event)} onChange={props.changeValue}/>
        </div>
    )
}
export default person
