//reducer作用：返回新的状态
import { FETCH_POSTS,NEW_POST } from '../actions/type'

const initialState = {
    items:[],
    item:{}
}
export default function(state = initialState, action){
    switch(action.type){
        case FETCH_POSTS:
            return {
                ...state,
                items:action.payload
            }
        case NEW_POST:
                return {
                    ...state,
                    items:action.payload
                }
        default:
            return state;
    }
}
