import React, { Component } from 'react'
import { connect } from 'react-redux'
import { sendPost } from '../actions/postActions'

class PostForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            title:'',
            body:''
        }
        this.onChange = this.onChange.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    onSubmit(e){
        e.preventDefault();
        const post = {
            title:this.state.title,
            body:this.state.body
        };
        this.props.sendPost(post)
    }
    onChange(e){
        this.setState({
            [e.target.name] : e.target.value
        })
    }
    render() {
        return (
            <div>
                <h1>添加POST</h1>
                <form onSubmit = {this.onSubmit}>
                    <div>
                        <label>title:</label>
                        <input onChange={this.onChange} type="text" value={this.state.title} name="title"/>
                    </div>
                    <div>
                        <label>body:</label>
                        <textarea onChange={this.onChange}  value={this.state.body} name="body"/>
                    </div>
                    <button type="submit">添加</button>
                </form>
            </div>
        )
    }
}
const mapStateToProps = state => ({
    post:state.postReducer.item
})
export default connect(mapStateToProps,{ sendPost })(PostForm)
